import './App.css';
import { useState } from "react";
const CONTAINER_COLOURS = ["RED", "GREEN", "BLUE", "BLACK", "WHITE"]

function App() {
  const [gameData, setGameData] = useState("");
  const [gameID, setGameID] = useState(0);

  let showFile = async (e) => {
    e.preventDefault()
    const reader = new FileReader()
    reader.onload = async (e) => { 
      const text = (e.target.result);
      console.log(text);
      let games = text.split('>');
      setGameData(games);
    };
    reader.readAsText(e.target.files[0])
  }

  let PRODUCE = [];
  let WARES = [];
  if (gameData != ""){
    let data = gameData[gameID].split('-');
    //(turn, price, colour, seller, buyer)
    PRODUCE = 
    [<table className='Table'>
      <tr>
        <td>Turn</td>
        <td>Colour</td>
        <td>Price</td>
        <td>Seller</td>
        <td>Buyer</td>
      </tr>
      {data[0].split('), (').map(item => Produce_itemise(item))}
    </table>]

    //(turn, price, colour, seller, buyer)
    WARES = 
    [<table className='Table'>
      <tr>
        <td>Turn</td>
        <td>Colour</td>
        <td>Price</td>
        <td>Seller</td>
        <td>Buyer</td>
      </tr>
      {data[1].split('), (').map(item => Produce_itemise(item))}
    </table>]
  }

  return (
    <div className="root">
      <div className="topbar">
        <div>Game P,W Scores</div>
        <input type="file" onChange={(e) => showFile(e)} />
        <button onClick={() => setGameID(gameID - 1)}>{"<"}</button>
        <div>Game {gameID}</div>
        <button onClick={() => setGameID(gameID + 1)}>{">"}</button>
      </div>
      <div className="bar1">
        Produce
        {gameData != "" && PRODUCE}
      </div>
      <div className="bar2">
        Wares
        {gameData != "" && WARES}
      </div>
      <div className="bar3">
        Auction
      </div>
      <div className="bar4">
        History
      </div>
    </div>
  );
}

function Produce_itemise(item){
  if (item[0] === "["){item = item.slice(2,)}
  else if (item[item.length-1] === "]"){item = item.slice(0,item.length-2)}
  let data = item.split(', ') //(turn, price, colour, seller, buyer)
  return(
    <tr>
      <td>Turn {data[0]} </td>
      <td style={{background:CONTAINER_COLOURS[data[2]]}}></td>
      <td>${data[1]}</td>
      <td>{data[3]}</td>
      <td>{data[4]}</td>
    </tr>
  )
}

export default App;
