import logo from './logo.svg';
import './App.css';

function App() {

  let showFile = async (e) => {
    e.preventDefault()
    const reader = new FileReader()
    reader.onload = async (e) => { 
      const text = (e.target.result)
      console.log(text)
      alert(text)
    };
    reader.readAsText(e.target.files[0])
  }


  return (
    <div className="root">
      <div className="topbar">Game P,W Scores</div>
      <input type="file" onChange={(e) => showFile(e)} />
      <div className="bar1">
        Produce
      </div>
      <div className="bar2">
        Wares
      </div>
      <div className="bar3">
        Auction
      </div>
      <div className="bar4">
        History
      </div>
    </div>
  );
}

export default App;
