import './App.css';
import { useState } from "react";


function App() {
  const [gameData, setGameData] = useState("");
  const [gameID, setGameID] = useState(0);

  let showFile = async (e) => {
    e.preventDefault()
    const reader = new FileReader()
    reader.onload = async (e) => { 
      const text = (e.target.result);
      console.log(text);
      let games = text.split('>');
      setGameData(games);
    };
    reader.readAsText(e.target.files[0])
  }

  let PRODUCE = [];
  if (gameData != ""){
    let data = gameData[gameID].split('-');
    PRODUCE = [
      data[0].split('), (').map(item => Produce_itemise(item))
    ]
  }


  return (
    <div className="root">
      <div className="topbar">
        <div>Game P,W Scores</div>
        <input type="file" onChange={(e) => showFile(e)} />
        <button onClick={() => setGameID(gameID - 1)}>{"<"}</button>
        <div>Game {gameID}</div>
        <button onClick={() => setGameID(gameID + 1)}>{">"}</button>
      </div>
      <div className="bar1">
        Produce
        {gameData != "" && PRODUCE}
      </div>
      <div className="bar2">
        Wares
      </div>
      <div className="bar3">
        Auction
      </div>
      <div className="bar4">
        History
      </div>
    </div>
  );
}

function Produce_itemise(item){
  if (item[0] === "["){item = item.slice(2,)}
  else if (item[item.length] === "]"){item = item.slice(-2,)}
  let data = item.split(', ')
  return(
    <div>Turn {data[0]} </div>
  )
}

export default App;
