import './App.css';
import { useState } from "react";
const CONTAINER_COLOURS = ["RED", "GREEN", "BLUE", "BLACK", "WHITE"]
const ALT_COLOURS = ["LIGHTRED", "greenyellow", "AQUA", "GREY", "beige"]
self.ACTION_KEY = ["Produce","Buy Produce", "Buy Wares","Sail Sea","Buy Fact","Buy Warehouse","Auction","Pass"]

function App() {
  const [gameData, setGameData] = useState("");
  const [gameID, setGameID] = useState(0);

  let showFile = async (e) => {
    e.preventDefault()
    const reader = new FileReader()
    reader.onload = async (e) => { 
      const text = (e.target.result);
      //console.log(text);
      let games = text.split('>');
      setGameData(games);
    };
    reader.readAsText(e.target.files[0])
  }

  let PRODUCE = [];
  let WARES = [];
  let AUCTION = [];
  if (gameData != "" && gameID != 0){
    let data = gameData[gameID].split('-');
    //(turn, price, colour, seller, buyer)
    PRODUCE = 
    [<table className='Table'>
      <tr>
        <td>Turn</td>
        <td>Colour</td>
        <td>Price</td>
        <td>Seller</td>
        <td>Buyer</td>
      </tr>
      {data[0].split('), (').map(item => Produce_itemise(item))}
    </table>];

    //(turn, price, colour, seller, buyer)
    WARES = 
    [<table className='Table'>
      <tr>
        <td>Turn</td>
        <td>Colour</td>
        <td>Price</td>
        <td>Seller</td>
        <td>Buyer</td>
      </tr>
      {data[1].split('), (').map(item => Produce_itemise(item))}
    </table>];

    //(turn, price, bundle of product, seller, buyer)
    AUCTION = 
    [<table className='Table'>
      <tr>
        <td>Turn</td>
        <td>Colour</td>
        <td>Price</td>
        <td>Seller</td>
        <td>Buyer</td>
      </tr>
      {data[2].split('), (').map(item => Auction_itemise(item))}
    </table>];

    HISTORY = data[3].map(item => <div>{item}</div>)
  }

  return (
    <div className="root">
      <div className="topbar">
        <div>Game P,W Scores</div>
        <input type="file" onChange={(e) => showFile(e)} />
        <button onClick={() => setGameID(gameID - 1)}>{"<"}</button>
        <div>Game {gameID}</div>
        <button onClick={() => setGameID(gameID + 1)}>{">"}</button>
      </div>
      <div className="bar1">
        Produce
        {gameData != "" && PRODUCE}
      </div>
      <div className="bar2">
        Wares
        {gameData != "" && WARES}
      </div>
      <div className="bar3">
        Auction
        {gameData != "" && AUCTION}
      </div>
      <div className="bar4">
        History
      </div>
    </div>
  );
}

function Produce_itemise(item){
  if (item[0] === "["){item = item.slice(2,)}
  else if (item[item.length-1] === "]"){item = item.slice(0,item.length-2)}
  let data = item.split(', ') //(turn, price, colour, seller, buyer)
  return(
    <tr>
      <td>Turn {data[0]} </td>
      <td style={{background:ALT_COLOURS[data[2]]}}></td>
      <td>${data[1]}</td>
      <td>{data[3]}</td>
      <td>{data[4]}</td>
    </tr>
  )
}

function Auction_itemise(item){
  if (item[0] === "["){item = item.slice(2,)}
  else if (item[item.length-1] === "]"){item = item.slice(0,item.length-2)}
  let first = item.split(", [")
  let left = first[0].split(", ")
  let second = first[1].split("], ")
  let middle = second[0].split(", ")
  let right = second[1].split(", ")

  let COLOURS = [];
  for (let i=0;i<middle.length;i++){
    COLOURS.push(<div style={{background:ALT_COLOURS[i], display:"inline-flex"}}>{middle[i]}</div>)
  }


  /* for (let i=0;i<positions;i++){
    colour += 
  } */
  return(
    <tr>
      <td>Turn {left[0]} </td>
      <td>
        {COLOURS}
      </td>
      <td>${left[1]}</td>
      <td>{right[0]}</td>
      <td>{right[1]}</td>
    </tr>
  )
}

export default App;
