import './App.css';
import { useState } from "react";


function App() {
  const [gameData, setGameData] = useState("");
  const [gameID, setGameID] = useState(0);

  let showFile = async (e) => {
    e.preventDefault()
    const reader = new FileReader()
    reader.onload = async (e) => { 
      const text = (e.target.result);
      console.log(text);
      let games = text.split('>');
      setGameData(games);
    };
    reader.readAsText(e.target.files[0])
  }

  let PRODUCE = [];
  if (gameData != ""){
    let data = gameData[gameID].split('-');
    console.log(data)
    PRODUCE = <div>{data[0]}</div>
    /* const PRODUCE = [
      data[0].split('-').map(item => Produce_itemise(item))
    ] */
  }


  return (
    <div className="root">
      <div className="topbar">Game P,W Scores</div>
      <input type="file" onChange={(e) => showFile(e)} />
      <div onClick={setGameID(gameID + 1)}>{">"}</div>
      <div>Game {gameID}</div>
      <div onClick={setGameID(gameID - 1)}>{"<"}</div>
      <div className="bar1">
        Produce
        {gameData != "" && PRODUCE}
      </div>
      <div className="bar2">
        Wares
      </div>
      <div className="bar3">
        Auction
      </div>
      <div className="bar4">
        History
      </div>
    </div>
  );
}

export default App;
